				\setlength{\oddsidemargin}{-69pt}
				\begin{document}
$$ {K_{1} (i) = {{k_{1} (i)} \over {\max (k_{1} )}}\quad{\mathop{\rm and}\nolimits}\quad K_{2} (i) = {{k_{2} (i)} \over {\max (k_{2} )}}.} $$\end{document}

